-- Миграция 018: Коэффициенты и надбавки СБЦ ИГДИ 2004 (ОУ, табл.1-6,10,78-80, примечания табл.9)
DO $$
DECLARE
    v_doc_id UUID;
BEGIN
    SELECT id INTO v_doc_id FROM norm_docs WHERE code = 'SBC_IGDI_2004';
    IF v_doc_id IS NULL THEN
        RAISE EXCEPTION 'Документ SBC_IGDI_2004 не найден. Сначала выполните миграцию 017.';
    END IF;

    DELETE FROM norm_coeffs WHERE doc_id = v_doc_id;
    DELETE FROM norm_addons WHERE doc_id = v_doc_id;



    RAISE NOTICE 'Коэффициенты добавлены: %', (SELECT COUNT(*) FROM norm_coeffs WHERE doc_id = v_doc_id AND (source_ref->>'source') = 'rtf_2004');
    RAISE NOTICE 'Надбавки добавлены: %', (SELECT COUNT(*) FROM norm_addons WHERE doc_id = v_doc_id AND (source_ref->>'source') = 'rtf_2004');
END $$;