-- Миграция 019: Приложения 1-5 (региональные коэффициенты и периоды)
DO $$
DECLARE
    v_doc_id UUID;
BEGIN
    SELECT id INTO v_doc_id FROM norm_docs WHERE code = 'SBC_IGDI_2004';
    IF v_doc_id IS NULL THEN
        RAISE EXCEPTION 'Документ SBC_IGDI_2004 не найден. Сначала выполните миграцию 017.';
    END IF;

    CREATE TABLE IF NOT EXISTS regional_desert_coeffs (
        id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
        doc_id uuid NOT NULL REFERENCES norm_docs(id) ON DELETE CASCADE,
        region_name text NOT NULL,
        subregion_name text,
        coeff numeric(8,4) NOT NULL,
        source_ref jsonb DEFAULT '{}'::jsonb,
        created_at timestamptz DEFAULT now(),
        updated_at timestamptz DEFAULT now()
    );

    CREATE TABLE IF NOT EXISTS regional_unfavorable_periods (
        id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
        doc_id uuid NOT NULL REFERENCES norm_docs(id) ON DELETE CASCADE,
        region_name text NOT NULL,
        period_start text,
        period_end text,
        duration_months numeric(5,2) NOT NULL,
        source_ref jsonb DEFAULT '{}'::jsonb,
        created_at timestamptz DEFAULT now(),
        updated_at timestamptz DEFAULT now()
    );

    CREATE TABLE IF NOT EXISTS regional_zone_lists (
        id uuid PRIMARY KEY DEFAULT gen_random_uuid(),
        doc_id uuid NOT NULL REFERENCES norm_docs(id) ON DELETE CASCADE,
        zone_type text NOT NULL,
        region_name text NOT NULL,
        subregion_name text,
        source_ref jsonb DEFAULT '{}'::jsonb,
        created_at timestamptz DEFAULT now(),
        updated_at timestamptz DEFAULT now()
    );

    DELETE FROM regional_desert_coeffs WHERE doc_id = v_doc_id;
    DELETE FROM regional_unfavorable_periods WHERE doc_id = v_doc_id;
    DELETE FROM regional_zone_lists WHERE doc_id = v_doc_id;




    RAISE NOTICE 'Приложения загружены.';
END $$;